import React, { useEffect, useState } from 'react';
import './Styles/ID.css'; // Импортируйте CSS файл
import { useNavigate } from "react-router-dom";

const Id = ({ message }) => {
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true); // Добавляем состояние загрузки
    const navigate = useNavigate(); // Используем хук для навигации, если нужно

    useEffect(() => {
        jsonFromBack();
    }, [message]); // Добавляем зависимость от message, чтобы запрос выполнялся при его изменении

    const jsonFromBack = async () => {
        setLoading(true); // Устанавливаем состояние загрузки перед началом запроса
        try {
            const response = await fetch(`https://localhost:8080/api/programInfo/${message}`, {
                method: 'GET',
                credentials: "include"
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false); // Сбрасываем состояние загрузки после завершения запроса
        }
    };

    if (loading) {
        return <div>Loading...</div>; // Показываем индикатор загрузки
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <table border="1" cellPadding="5" cellSpacing="0">
            <thead>
            <tr>
                <th>Author</th>
                <th>Version</th>
                <th>Name</th>
            </tr>
            </thead>
            <tbody>
            {Array.isArray(data) && data.length > 0 ? (
                data.map((item, index) => (
                    <tr key={index}>
                        <td>{item.Author}</td>
                        <td>{item.Version}</td>
                        <td>{item.Name}</td>
                    </tr>
                ))
            ) : (
                <tr>
                    <td colSpan="3">No data available</td>
                </tr>
            )}
            </tbody>
        </table>
    );
};

export default Id;
