import React from 'react';
import { useNavigate } from 'react-router-dom';
import './App.css';

const LoginPage = () => {
  const navigate = useNavigate();

  const redirectToUrl = () => {
    const userId = document.getElementById('userId').value;
    if (userId > 0 && userId <= 100) {
      const newUrl = `/api/${userId}`;
      history.push(newUrl);
    } else {
      alert('Please enter a valid ID');
    }

    const redirectToNext = () =>{
      navigate('/login');
    }

  };

  return (
    <div className="container">
      <button className="top-left-button" onClick={redirectToNext}>
        Зайти
      </button>
      <div className="form-container">
        <h1>Enter ID</h1>
        <input type="text" id="userId" placeholder="Enter your ID" />
        <button onClick={redirectToUrl}>Go</button>
      </div>
      <button className="Reg_button" onClick={redirectToNext}>
        Зарегаться
      </button>
      <button className="All_button" onClick={redirectToNext}>
        All
      </button>
      <button className="top-left-button1" onClick={redirectToNext}>
        Выйти
      </button>
      <button className="top-left-button2" onClick={redirectToNext}>
        Профиль
      </button>
      <button className="top-left-button3" onClick={redirectToNext}>
        Музон
      </button>
    </div>
  );
};

export default HomePage;
