import React, { useState } from 'react';
import './Styles/Update.css';
import { useNavigate } from "react-router-dom";

const Update = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [tele, setTele] = useState('');
    const [git, setGit] = useState('');
    const [file, setFile] = useState(null);
    const [editing, setEditing] = useState({ name: false, password: false, tele: false, git: false });
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        if (name) formData.append('name', name);
        if (password) formData.append('password', password);
        if (tele) formData.append('tele', tele);
        if (git) formData.append('git', git);
        if (file) formData.append('file', file);

        const json = JSON.stringify({ name, password, tele, git });
        formData.append('json', new Blob([json], { type: 'application/json' }));

        const response = await fetch('https://localhost:8080/api/user', {
            method: 'PATCH',
            body: formData,
            credentials: "include"
        });

        if (response.ok) {
            alert('Data sent successfully!');
        } else {
            alert('Failed to send data.');
        }
    };

    const handleEditToggle = (field) => {
        setEditing((prevEditing) => ({
            ...prevEditing,
            [field]: !prevEditing[field]
        }));
    };

    const handleBlur = (field, value) => {
        setEditing((prevEditing) => ({
            ...prevEditing,
            [field]: false
        }));
        if (value) {
            // Save value to state or perform any other action if needed
            if (field === 'name') setName(value);
            if (field === 'password') setPassword(value);
            if (field === 'tele') setTele(value);
            if (field === 'git') setGit(value);
        }
    };

    const useRedirect = () => {
        navigate('/');
    };

    return (
        <div className="form-container">
            <h1>Enter Data</h1>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                {['name', 'password', 'tele', 'git'].map((field) => (
                    <div key={field} className="field-container">
                        {editing[field] ? (
                            <input
                                type={field === 'password' ? 'password' : 'text'}
                                value={field === 'name' ? name : field === 'password' ? password : field === 'tele' ? tele : git}
                                onChange={(e) => {
                                    const { value } = e.target;
                                    if (field === 'name') setName(value);
                                    if (field === 'password') setPassword(value);
                                    if (field === 'tele') setTele(value);
                                    if (field === 'git') setGit(value);
                                }}
                                onBlur={() => handleBlur(field, field === 'name' ? name : field === 'password' ? password : field === 'tele' ? tele : git)}
                                autoFocus
                            />
                        ) : (
                            <div className="field-display" onClick={() => handleEditToggle(field)}>
                                {field === 'name' ? name : field === 'password' ? password : field === 'tele' ? tele : git || 'Click to edit'}
                            </div>
                        )}
                    </div>
                ))}
                <div className="file-input-container">
                    <label className="file-input-label" htmlFor="file">Выберите новый аватар</label>
                    <input
                        type="file"
                        id="file"
                        name="file"
                        onChange={(e) => setFile(e.target.files[0])}
                    />
                </div>
                <button type="submit">Send</button>
            </form>
            <button className="Back" onClick={useRedirect}>Назад</button>
        </div>
    );
};

export default Update;
