import React, { useState } from 'react';
import './Styles/Registr.css'
import {useNavigate} from 'react-router-dom'

const UserForm = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [roles, setRoles] = useState('');
    const [file, setFile] = useState(null);
    const [check,setcheck] = useState(true);

    const navigate =useNavigate();
    const handleSubmit = async (event) => {
        event.preventDefault(); // предотвращаем стандартное поведение отправки формы

        const formData = new FormData();
        formData.append('name', name);
        formData.append('password', password);
        formData.append('roles', roles);
        formData.append('file', file);

        try {
            const response = await fetch('https://localhost:8080/api/user', {
                method: 'POST',
                body: formData
            });

            if (response.ok) {
               navigate('/') ;
            } else {
                alert('Ошибка при отправке данных');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('Ошибка при отправке данных');
        }
    };
    const checking=async () => {
        const response = await fetch('https://localhost:8080/api/checking',{method:'GET'})
        if(response.status()===200)
            setcheck(false)
        else
            setcheck(true)

    }

    return (
        <div className="form-container">
            <h1>Enter Data</h1>
            <form id="userForm" onSubmit={handleSubmit} encType="multipart/form-data">
                <input
                    className={check ?'1' :'2'}
                    type="text"
                    id="name"
                    name="name"
                    placeholder="Enter your Name"
                    value={name}
                    onChange={checking}
                    required
                />
                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="Enter your Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <input
                    type="text"
                    id="roles"
                    name="roles"
                    placeholder="Enter your Role"
                    value={roles}
                    onChange={(e) => setRoles(e.target.value)}
                    required
                />
                <div className="file-input-container">
                    <label className="file-input-label" htmlFor="file">Выберите аватар</label>
                    <input
                        type="file"
                        id="file"
                        name="file"
                        onChange={(e) => setFile(e.target.files[0])}
                        required
                    />
                </div>
                <button id="but" type="submit">Send</button>
            </form>
            <button className="Back" onClick={() => navigate('/')}>Назад</button>
        </div>
    );
};

export default UserForm;
