import React, { useEffect, useState } from 'react';
import './Styles/Profile.css';
import { useNavigate } from 'react-router-dom';
import Menu from "./Menu";

const Profile = () => {
    const navigate = useNavigate();
    const[menuActive,setMenuActive]=useState(false);
    const[isChecked,setIsChecked]=useState(false);

    const[userImage,setUserImage]=useState('12');
    const redirectTo = (url) => {
        navigate(url);
    };


    const userInfo=async ()=> {
        try {
            const response = await fetch('https://localhost:8080/api/userInfo',{
                method:'GET',credentials:'include'});
            const user=response.text()
            setUserImage(await user)
        } catch (error) {
            console.error('Error:', error);

        }
    }
    useEffect(()=>
    {
        userInfo();
    });
    const handleChange = (event) => {
        const checked = event.target.checked;
        setIsChecked(checked);

        if (checked) {
            document.body.style.backgroundColor = 'lightgrey';
        } else {
            document.body.style.backgroundColor = 'white';
        }
    };



        return (
            <div >


                <nav>

                    <div className='burger-btn' onClick={() => setMenuActive(!menuActive)}>

                        <span className={menuActive ? 'deva' : 'deva active'}/>

                    </div>

                </nav>
                <main>
                    <div>
                        <img id="myImage" src={`https://localhost:8080/api/images/${userImage}`}
                             alt="Profile"/>
                    </div>

                </main>

                <button className="back_up" onClick={() => redirectTo('/')}>Назад</button>
                <div className="toggle-switch">
                    <input type="checkbox" id="toggle" className="toggle-input"checked={isChecked}
                           onChange={handleChange}/>
                    <label htmlFor="toggle" className="toggle-label" ></label>
                </div>
                <Menu active={menuActive} setActive={setMenuActive}/>

            </div>
        );


};

export default Profile;
