import React from 'react';
import { useHistory } from 'react-router-dom';
import './App.css'; // Предполагается, что стили находятся в App.css

const LoginPage = () => {
  const history = useHistory();

  const redirectTo = (url) => {
    // Перенаправление на указанный URL
    window.location.href = url;
  };

  return (
    <div className="container">
      <button
        className="top-left-button"
        onClick={() => redirectTo('http://localhost:3000/')}
      >
        Назад
      </button>

      <form action="/perform_login" method="post" content="application/x-www-form-urlencoded">
        <div className="naming">
          <img
            src="https://github.githubassets.com/images/modules/logos_page/GitHub-Mark.png"
            alt="GitHub Logo"
            className="logo"
          />
          <h1>Sign in to Base</h1>

          <input
            type="text"
            id="name"
            name="name"
            placeholder="Enter your Name"
            required
          />

          <input
            type="password"
            id="password"
            name="password"
            placeholder="Enter your password"
            required
          />

          <button className="button" type="submit">
            Login
          </button>
        </div>
      </form>
    </div>
  );
};

export default LoginPage;
