import React, { useState } from 'react';
import './Styles/Update.css';
import { useNavigate } from "react-router-dom";
import logo from './logo.svg';  // Import the default logo

const Update = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [tele, setTele] = useState('');
    const [git, setGit] = useState('');
    const [file, setFile] = useState(null);  // Start with no file selected
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        if (name) formData.append('name', name);
        if (password) formData.append('password', password);
        if (tele) formData.append('tele', tele);
        if (git) formData.append('git', git);

        // Append the file if it exists, otherwise append the default logo
        formData.append('file', file || new Blob([logo], { type: 'image/svg+xml' }));

        const json = JSON.stringify({ name, password, tele, git });
        formData.append('json', new Blob([json], { type: 'application/json' }));

        const response = await fetch('https://localhost:8080/api/user', {
            method: 'PATCH',
            body: formData,
            credentials: "include"
        });

        if (response.ok) {
            alert('Data sent successfully!');
        } else {
            alert('Failed to send data.');
        }
    };

    const useRedirect = () => {
        navigate('/');
    };

    return (
        <div className="form-container">
            <h1>Enter Data</h1>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <input
                    type="text"
                    id="name"
                    name="name"
                    placeholder="Enter your Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="Enter your Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <input
                    type="text"
                    id="tele"
                    name="tele"
                    placeholder="Enter your Telegram"
                    value={tele}
                    onChange={(e) => setTele(e.target.value)}
                />
                <input
                    type="text"
                    id="git"
                    name="git"
                    placeholder="Enter your GitHub"
                    value={git}
                    onChange={(e) => setGit(e.target.value)}
                />
                <div className="file-input-container">
                    <label className="file-input-label" htmlFor="file">Выберите новый аватар</label>
                    <input
                        type="file"
                        id="file"
                        name="file"
                        onChange={(e) => setFile(e.target.files[0])}
                    />
                </div>
                <button type="submit">Send</button>
            </form>
            <button className="Back" onClick={useRedirect}>Назад</button>
        </div>
    );
};

export default Update;
