// src/useAuth.js
import { useState, useEffect } from 'react';
import axios from 'axios';

const useAuth = () => {
    const [auth, setAuth] = useState(false);

    useEffect(() => {
        axios.get('https://localhost:8080/api/authorization')
            .then(response => {
                if (response.status === 200) {
                    setAuth(true);
                }
            })
            .catch(() => {
                setAuth(false);
            });
    }, []);

    return { auth };
};

export default useAuth;
