import React from 'react';
import './App.css';

const HomePage = () => {
  const redirectToUrl = () => {
    const userId = document.getElementById('userId').value;
    if (userId > 0 && userId <= 100) {
      const newUrl = `http://localhost:8080/api/${userId}`;
      window.location.href = newUrl;
    } else {
      alert('Please enter a valid ID');
    }
  };

  return (
    <div className="container">
      <button className="top-left-button" onClick={() => window.location.href='http://localhost:8080/api/login'}>
        Зайти
      </button>
      <div className="form-container">
        <h1>Enter ID</h1>
        <input type="text" id="userId" placeholder="Enter your ID" />
        <button onClick={redirectToUrl}>Go</button>
      </div>
      <button className="Reg_button" onClick={() => window.location.href='http://localhost:8080/api/newUser'}>
        Зарегаться
      </button>
      <button className="All_button" onClick={() => window.location.href='http://localhost:8080/api/All'}>
        All
      </button>
      <button className="top-left-button1" onClick={() => window.location.href='http://localhost:8080/api/logout'}>
        Выйти
      </button>
      <button className="top-left-button2" onClick={() => window.location.href='http://localhost:8080/api/Profile'}>
        Профиль
      </button>
      <button className="top-left-button3" onClick={() => window.location.href='http://localhost:8080/api/audioList'}>
        Музон
      </button>
    </div>
  );
};

export default HomePage;
