import { useEffect, useState } from 'react';
import './Styles/Profile.css';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    const [imagePath, setImagePath] = useState('12');
    const navigate = useNavigate();
    const redirectToLogout = () => {
        window.location.replace('https://localhost:8080/api/logout');
    };

    const Toolbar = () => {
        const [isOpen, setIsOpen] = useState(false);

        const toggleToolbar = () => {

            setIsOpen(!isOpen);
        };

        useEffect(() => {
            getUserInfo();
        }, []);

        const getUserInfo = async () => {
            try {
                const response = await fetch('https://localhost:8080/api/userInfo', {
                    headers: {
                        'Cache-Control': 'no-cache',
                    },
                    credentials: "include"
                });
                if (response.ok) {
                    const data = await response.text();
                    setImagePath(data);
                } else if (response.status === 304) {
                    console.log('Resource not modified.');
                } else {
                    console.error('Error fetching user info', response.status);
                }
            } catch (error) {
                console.error('Error fetching user info', error);
            }
        };

        const redirectTo = (url) => {
            navigate(url);
        };

        const redirectToDelete = async () => {
            try {
                if (window.confirm('Вы уверены, что хотите удалить свою учетную запись?')) {
                    const response = await fetch('https://localhost:8080/api/user', {
                        method: 'DELETE',
                        credentials: 'include'
                    });
                    if (response.ok)
                        window.location.replace('https://localhost:8080/api/logout');
                }
            } catch (error) {
                console.error('Error deleting', error);
                alert('Error deleting.');
            }
        };

        return (
            <div>
                <div className="image-container">
                    <img id="myImage" className="image" src={`https://localhost:8080/api/images/${imagePath}`} alt="Profile"/>
                </div>
                <div className="toolbar-container">
                    <a onClick={toggleToolbar} className="toolbar-toggle-btn">
                        <img  className="img_tool" src="https://cdn1.iconfinder.com/data/icons/flat-design-basic-set-7/24/menu-list-512.png"/>
                    </a>
                    <div className={`toolbar-content ${isOpen ? 'open' : 'closed'}`}>
                        <ul>
                            <li><a onClick={() => redirectTo('/')}>Назад</a></li>
                            <li><a onClick={redirectToDelete}>Удалить учетную запись</a></li>
                            <li><a onClick={() => redirectTo('/update')}>Обновить мои данные</a></li>
                            <li ><a className="exit" onClick={redirectToLogout}>Выйти</a></li>
                        </ul>
                    </div>
                </div>
            </div>
        );
    }

    return <Toolbar />;
}

export default Profile;
