import React, { useEffect, useState } from 'react';
import './Profile.css';
import {useNavigate} from 'react-router-dom'

function Profile() {
    const [imagePath, setImagePath] = useState('');
    const [userData, setUserData] = useState({
        name: '',
        password: '',
        role: ''
    });
    const navigate = useNavigate();

    useEffect(() => {
        getUserInfo();
    }, []);

    const getUserInfo = async () => {
        try {
            const response = await fetch('https://localhost:8080/api/userInfo', {
                method: 'GET'
            });

            if (response.ok) {
                const data = await response.json();
                setImagePath(data.imagePath);
                setUserData({
                    name: data.name,
                    password: '',
                    role: data.role
                });
            } else {
                console.error('Server returned an error:', response.status, response.statusText);
                alert("Error fetching user info.");
            }
        } catch (error) {
            console.error('Error fetching user info:', error);
            alert("Error fetching user info.");
        }
    };

    const redirectTo = (url) => {
        navigate(url);
    };

    const redirectToDelete = async () => {
        try {
            if (window.confirm('Вы уверены, что хотите удалить свою учетную запись?')) {
                const response = await fetch('https://localhost:8080/api/user', {
                    method: 'DELETE'
                });
                if (response.ok) {
                    let windowExit = window.open("https://localhost:8080/api/logout");
                    window.close();
                }
            }
        } catch (error) {
            console.error('Error deleting', error);
            alert("Error deleting.");
        }
    };

    return (
        <div>
            <div className="form-container">
                <form>
                    <label htmlFor="name">Имя:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={userData.name}
                        onChange={(e) => setUserData({ ...userData, name: e.target.value })}
                    />

                    <label htmlFor="password">Пароль:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={userData.password}
                        onChange={(e) => setUserData({ ...userData, password: e.target.value })}
                    />

                    <label htmlFor="role">Роль:</label>
                    <input
                        type="text"
                        id="role"
                        name="role"
                        value={userData.role}
                        onChange={(e) => setUserData({ ...userData, role: e.target.value })}
                    />
                </form>
            </div>
            <div className="image-container">
                <input type="file" id="imageInput" accept="image/*" style={{ display: 'none' }} />
                <img id="myImage" className="image" src={`https://localhost:8080/api/images/${imagePath}`} alt="Profile" />
            </div>
            <button className="top-left-button" onClick={() => redirectTo("/")}>Назад</button>
            <button className="top-left-button1" onClick={redirectToDelete}>Удалить учетную запись</button>
            <button className="Up_button" onClick={()=>redirectTo('update')}>Update my info</button>
        </div>
    );
}

export default Profile;
