import React, { useEffect, useState } from 'react';
import './Styles/ID.css'; // Импортируйте CSS файл
import { useLocation, useNavigate } from "react-router-dom";
import './Styles/All.css';
const All = () => {
    const location = useLocation();
    const { inputValue } = location.state || { inputValue: 'No ID provided' };
    const [data, setData] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true); // Добавляем состояние загрузки
    const navigate = useNavigate(); // Используем хук для навигации, если нужно

    useEffect(() => {
        jsonFromBack();
    }, []); // Добавляем зависимость от inputValue, чтобы запрос выполнялся при его изменении

    const redirectTo = (url) => {
        navigate(url);
    };

    const jsonFromBack = async () => {
        setLoading(true); // Устанавливаем состояние загрузки перед началом запроса
        try {
            const response = await fetch(`https://localhost:8080/api/All`, {
                method: 'GET',
                credentials: "include"
            });

            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }

            const result = await response.json();
            setData(result);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false); // Сбрасываем состояние загрузки после завершения запроса
        }
    };

    if (loading) {
        return <div>Loading...</div>; // Показываем индикатор загрузки
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <table1 border="1" cellPadding="5" cellSpacing="0">
                <thead1>
                <tr1>
                    <th1>Author</th1>
                    <th1>Name</th1>
                    <th1>Version</th1>
                </tr1>
                </thead1>
                <tbody1>
                {data.length > 0 ? (
                    data.map((item, index) => (
                        <tr key={index}>
                            <td>{item.author}</td>
                            <td>{item.name}</td>
                            <td>{item.version}</td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="3">No data available</td>
                    </tr>
                )}
                </tbody1>
            </table1>
            <button className="back_up1" onClick={() => redirectTo('/')}>Назад</button>
        </div>
    );
};

export default All;
