import React, { useState, useEffect, useRef } from 'react';

const AudioPlayer = () => {
  const [counter, setCounter] = useState(152);
  const [header, setHeader] = useState('Loading header...');
  const audioRef = useRef(null);

  useEffect(() => {
    loadAudio(counter);
  }, [counter]);

  const loadAudio = async (counter) => {
    const audioUrl = `https://localhost:8080/api/audio/${counter}`;
    const audioType = 'audio/mp3';

    if (audioRef.current) {
      const audioElement = audioRef.current;

      // Удаляем все текущие source элементы
      while (audioElement.firstChild) {
        audioElement.removeChild(audioElement.firstChild);
      }

      // Создаем новый элемент source
      const sourceElement = document.createElement('source');
      sourceElement.src = audioUrl;
      sourceElement.type = audioType;

      // Добавляем новый source элемент в элемент аудио
      audioElement.appendChild(sourceElement);

      // Устанавливаем название трека (если есть)
      try {
        const response = await fetch(`https://localhost:8080/api/audioName/${counter}`, { method: 'GET' });
        setHeader(await response.text());
      } catch (error) {
        console.error('Error fetching audio name:', error);
        setHeader('Error loading header');
      }

      // Перезагружаем аудио для его воспроизведения с новым источником
      try {
        audioElement.load();
        audioElement.play();
        audioElement.volume = 0.1;
      } catch (error) {
        console.error('Error playing audio:', error);
      }
    }
  };

  const nextAudio = () => {
    setCounter((prevCounter) => prevCounter + 50);
  };

  const prevAudio = () => {
    setCounter((prevCounter) => (prevCounter !== 152 ? prevCounter - 50 : prevCounter));
  };

  const redirectTo = (url) => {
    const newWindow = window.open(url);
    if (newWindow) {
      window.close();
    } else {
      alert('Unable to close the current window.');
    }
  };

  return (
    <div>
      <audio controls id="myAudio" ref={audioRef} onEnded={nextAudio} style={{ position: 'absolute', top: '70px', left: '10px', marginBottom: '10px' }}>
        <source src="" type="audio/mp3" />
      </audio>
      <div>
        <button type="button" className="button" id="first" onClick={nextAudio} style={buttonStyle}>Следующий</button>
        <button type="button" className="button1" id="second" onClick={prevAudio} style={button1Style}>Предыдущий</button>
        <button className="top-left-button" onClick={() => redirectTo('/')} style={topLeftButtonStyle}>Назад</button>
      </div>
      <div className="form-container" style={formContainerStyle}>
        <div id="header-value" style={headerValueStyle}>{header}</div>
      </div>
    </div>
  );
};

const buttonStyle = {
  position: 'absolute',
  top: '140px',
  left: '170px',
  backgroundColor: '#000',
  color: '#fff',
  padding: '10px 20px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '16px',
  transition: 'background-color 0.3s, color 0.3s',
};

const button1Style = {
  position: 'absolute',
  top: '140px',
  left: '20px',
  backgroundColor: '#000',
  color: '#fff',
  padding: '10px 20px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '16px',
  transition: 'background-color 0.3s, color 0.3s',
};

const topLeftButtonStyle = {
  position: 'absolute',
  top: '20px',
  right: '20px',
  backgroundColor: '#000',
  color: '#fff',
  padding: '10px 20px',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '16px',
  transition: 'background-color 0.3s, color 0.3s',
};

const formContainerStyle = {
  display: 'flex',
  flexDirection: 'column',
  alignItems: 'center',
  gap: '10px',
};

const headerValueStyle = {
  fontSize: '1.5em',
  color: 'black',
  alignSelf: 'flex-start',
  marginTop: '10px',
};

export default AudioPlayer;
