import { useEffect, useState } from 'react';
import './Styles/Profile.css';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    const [imagePath, setImagePath] = useState('12');
    const navigate = useNavigate();

    const redirectToLogout = () => {
        window.location.replace('https://localhost:8080/api/logout');
    };

    const Toolbar = () => {
        const [isOpen, setIsOpen] = useState(false);
        const [isRotated, setIsRotated] = useState(false);
        const [user, setUser] = useState(null);

        const toggleToolbar = () => {
            setIsOpen(!isOpen);
            setIsRotated(!isRotated);
        };

        useEffect(() => {
            getUserInfo();
            getUser();
        }, []);

        const getUser = async () => {
            try {
                const response = await fetch('https://localhost:8080/api/infoAboutUser', {
                    method: 'GET',
                    credentials: 'include'
                });

                    const userok = await response.json();
                    setUser(userok);

            } catch (error) {
                console.error('Error fetching user info', error);
            }
        };

        const getUserInfo = async () => {
            try {
                const response = await fetch('https://localhost:8080/api/userInfo', {
                    headers: {
                        'Cache-Control': 'no-cache',
                    },
                    credentials: 'include'
                });

                    const data = await response.text();
                    setImagePath(data);

            } catch (error) {
                console.error('Error fetching user info', error);
            }
        };

        const redirectTo = (url) => {
            navigate(url);
        };

        const redirectToDelete = async () => {
            try {
                if (window.confirm('Вы уверены, что хотите удалить свою учетную запись?')) {
                    const response = await fetch('https://localhost:8080/api/user', {
                        method: 'DELETE',
                        credentials: 'include'
                    });
                    if (response.ok)
                        window.location.replace('https://localhost:8080/api/logout');
                }
            } catch (error) {
                console.error('Error deleting', error);
                alert('Error deleting.');
            }
        };

        return (
            <div>

                <div>
                    <nav>

                      <div  className="burger-btn">
                          <span/>
                      </div>

                    </nav>
                    <main>
                        <ul>
                            {user && (
                                <>
                                    <li>Name: {user.name}</li>
                                    <li>Created: {new Date(user.created).toLocaleDateString()}</li>
                                    <li>Updated: {new Date(user.updated).toLocaleDateString()}</li>
                                    <li>Role: {user.roles}</li>
                                </>
                            )}
                            <li><a onClick={redirectToDelete}>Удалить учетную запись</a></li>
                            <li><a onClick={() => redirectTo('/update')}>Обновить мои данные</a></li>
                            <li><a className="exit" onClick={redirectToLogout}>Выйти</a></li>
                        </ul>
                    </main>


                </div>
                <button className="back_up" onClick={() => redirectTo('/')}>Назад</button>

            </div>
        );
    };

    return <Toolbar/>;
};

export default Profile;
