import React, { useEffect } from 'react';
import './Profile.css';
import { useNavigate } from 'react-router-dom';

const Profile = () => {
    let imagePath ='12';
    const navigate = useNavigate();

    useEffect(() => {
        getUserInfo();
    }, []);

    const getUserInfo = async () => {
        try {
            const response = await fetch('https://localhost:8080/userInfo', {
                method: 'GET',
                credentials: 'include'
            });

            if (response.ok) {
                const data = await response.text();
                 imagePath =data;
            } else {
            
                alert("Error fetching user info.");
            }
        } catch (error) {
            console.error('Error fetching user info:', error);
          
        }
    };

    const redirectTo = (url) => {
        navigate(url);
    };

    const redirectToDelete = async () => {
        try {
            if (window.confirm('Вы уверены, что хотите удалить свою учетную запись?')) {
                const response = await fetch('https://localhost:8080/api/user', {
                    method: 'DELETE'
                });
                if (response.ok) {
                    window.location.href = 'http://localhost:8080/api/logout';
                }
            }
        } catch (error) {
            console.error('Error deleting', error);
            alert("Error deleting.");
        }
    };

    return (
        <div>
            <div className="image-container">
                <img id="myImage" className="image" src={`https://localhost:8080/images/`+imagePath} alt="Profile" />
            </div>
            <button className="top-left-button2" onClick={() => redirectTo("/")}>Назад</button>
            <button className="top-left-button1" onClick={redirectToDelete}>Удалить учетную запись</button>
            <button className="Up_button" onClick={() => redirectTo('/update')}>Update my info</button>
        </div>
    );
}

export default Profile;
